import { useState } from "react"
import { ArrowComp } from "../assets/ArrowComp"
import '../assets/caret--left.svg'
import '../assets/monchev-logo.png'

export const Sidebar = () => {

    const [open, setOpen] = useState(true)

    const onToggleClick = () => {
        setOpen(!open)
    }

    return(
        <div className="flex">
            <div className={`${open ? 'w-64' : 'w-24'} p-5 pt-8 duration-500 h-screen bg-slate-950 relative`}>
            {/* Arrow icon */}
                <div onClick={onToggleClick}>
                    <ArrowComp open={open}/>
                </div>

            {/* Menu */}
            <div className="flex gap-x-4 items-center">
                {/* <img src="../assets/monchev-logo.png"  /> */}
                <h1 className="text-white font-bold text-lg ">MONCHEV-DESIGN</h1>

            </div>



            </div>



            <div className="p-7 text-2xl font-semibold flex-1 h-screen">
                <h1 onClick={onToggleClick} >HomePage</h1>
            </div>
        </div>
    )
}
